mapper.update:
update setmeal
name = #{name}, category_id = #{categoryId}, price = #{price}, status = #{status}, description = #{description}, image = #{image}, update_time = #{updateTime}, update_user = #{updateUser}
where id = #{id}

mapper.select:
select d.*,c.name as categoryName from setmeal d left outer join category c on d.category_id = c.id


        and d.name like concat('%',#{name},'%')


        and d.category_id = #{categoryId}


        and d.status = #{status}


order by create_time desc
